import{j as i}from"./index-tYRXnBGF.js";import{R as a}from"./react-vendor-DUMDxmrn.js";import{e as n,f as e}from"./icons-1Ng1m9w4.js";const s=({items:s,title:l="Dažniausiai užduodami klausimai",suppressSchema:t=!1,sanitizeAnswers:o=!0})=>{const[u,r]=a.useState(null),m=t?null:{"@context":"https://schema.org","@type":"FAQPage",mainEntity:s.map((i=>{return{"@type":"Question",name:i.question,acceptedAnswer:{"@type":"Answer",text:o?(a=i.answer,a.replace(/<[^>]*>/g,"").replace(/\s+/g," ").trim()):i.answer}};var a}))};return i.jsx("section",{className:"py-12 bg-gray-50",children:i.jsxs("div",{className:"max-w-3xl mx-auto px-4 sm:px-6 lg:px-8",children:[!t&&m&&i.jsx("script",{type:"application/ld+json",children:JSON.stringify(m)}),i.jsx("h2",{className:"text-3xl font-bold text-center mb-8",children:l}),i.jsx("div",{className:"space-y-4",children:s.map(((a,s)=>i.jsxs("div",{className:"bg-white rounded-lg shadow-sm hover:shadow-md transition-shadow duration-200",children:[i.jsxs("button",{className:"w-full px-6 py-4 flex items-center justify-between text-left focus:outline-none focus:ring-2 focus:ring-green-500 focus:ring-opacity-50 rounded-lg",onClick:()=>r(u===s?null:s),"aria-expanded":u===s,children:[i.jsx("span",{className:"font-semibold text-lg pr-8",children:a.question}),u===s?i.jsx(n,{className:"w-5 h-5 text-green-600 flex-shrink-0"}):i.jsx(e,{className:"w-5 h-5 text-green-600 flex-shrink-0"})]}),u===s&&i.jsx("div",{className:"px-6 pb-4 prose prose-green max-w-none",dangerouslySetInnerHTML:{__html:a.answer}})]},s)))})]})})},l=[{question:"Kaip išmesti seną šaldytuvą nemokamai?",answer:"\n      <p>Seną šaldytuvą galite išmesti nemokamai Vilniuje, Kaune ir visoje Lietuvoje keliais paprastais žingsniais:</p>\n      <ol>\n        <li>Užpildykite užklausos formą mūsų svetainėje arba paskambinkite telefonu</li>\n        <li>Sutarsime jums patogų išvežimo laiką</li>\n        <li>Mūsų specialistai atvyks ir profesionaliai išneš šaldytuvą</li>\n        <li>Pasirūpinsime ekologišku šaldytuvo perdirbimu</li>\n      </ol>\n      <p>Paslauga teikiama <strong>nemokamai</strong> didžiuosiuose miestuose ir daugelyje rajonų. Dėl konkrečios vietovės – susisiekite.</p>\n    "},{question:"Kur atiduoti neveikiančią skalbimo mašiną?",answer:'\n      <p>Neveikiančią skalbimo mašiną galite atiduoti mums – Vilniuje, Kaune ir visoje Lietuvoje pasirūpinsime viskuo nuo A iki Z:</p>\n      <ul>\n        <li>Nemokamas išvežimas iš bet kurio aukšto</li>\n        <li>Profesionalus atjungimas nuo komunikacijų</li>\n        <li>Saugus transportavimas</li>\n        <li>Ekologiškas perdirbimas</li>\n      </ul>\n      <p>Skambinkite <a href="tel:+37069925744">+370 699 25744</a> arba užpildykite užklausą internetu.</p>\n    '},{question:"Ar išvežate buitinę techniką savaitgaliais?",answer:"\n      <p>Šiuo metu dirbame darbo dienomis:</p>\n      <ul>\n        <li>I–V: 8:00 - 18:00</li>\n      </ul>\n      <p>Savaitgaliais galite palikti užklausą – susisieksime pirmą darbo dieną.</p>\n    "},{question:"Kokią buitinę techniką išvežate nemokamai?",answer:"\n      <p>Nemokamai išvežame visą stambią buitinę techniką:</p>\n      <ul>\n        <li>Šaldytuvus ir šaldiklius</li>\n        <li>Skalbimo mašinas</li>\n        <li>Indaploves</li>\n        <li>Virykles ir orkaites</li>\n        <li>Džiovykles</li>\n        <li>Mikrobangų krosneles</li>\n      </ul>\n      <p>Išvežimas nemokamas nepriklausomai nuo technikos būklės ar aukšto.</p>\n    "},{question:"Kaip paruošti šaldytuvą išvežimui?",answer:"\n      <p>Šaldytuvo paruošimas išvežimui yra paprastas procesas:</p>\n      <ol>\n        <li>Išjunkite šaldytuvą iš elektros tinklo bent 12 val. prieš išvežimą</li>\n        <li>Išimkite visus produktus ir atšildykite šaldytuvą</li>\n        <li>Išvalykite vidų nuo vandens ir nešvarumų</li>\n        <li>Išimkite lentynėles (nebūtina, bet rekomenduojama)</li>\n      </ol>\n      <p>Jei negalite atlikti šių veiksmų, mūsų komanda padės tai padaryti vietoje.</p>\n    "},{question:"Ar reikia išnešti buitinę techniką į lauką prieš išvežimą?",answer:"\n      <p>Ne, jums nereikia rūpintis technikos išnešimu:</p>\n      <ul>\n        <li>Mūsų specialistai išneš techniką iš bet kurio aukšto</li>\n        <li>Turime specialią įrangą saugiam transportavimui</li>\n        <li>Pasirūpiname lifto/laiptinės apsauga</li>\n        <li>Išnešame net per siaurus praėjimus</li>\n      </ul>\n      <p>Jums tereikia parodyti, kurią techniką reikia išvežti.</p>\n    "},{question:"Kiek laiko užtrunka buitinės technikos išvežimas?",answer:"\n      <p>Buitinės technikos išvežimo procesas yra greitas ir efektyvus:</p>\n      <ul>\n        <li>Atvykimas po užsakymo: 1-2 darbo dienos</li>\n        <li>Vienos technikos išnešimas: Iki 5 minučių</li>\n        <li>Kelių prietaisų išvežimas: Iki 10 minučių</li>\n      </ul>\n      <p>Tikslų laiką sutarsime pagal jūsų pageidavimus ir technikos kiekį.</p>\n    "},{question:"Kas nutinka su išvežta buitine technika?",answer:"\n      <p>Išvežta buitinė technika perdirbama ekologiškai atsakingu būdu:</p>\n      <ol>\n        <li>Technika pristatoma į licencijuotą perdirbimo centrą</li>\n        <li>Atskiriamos perdirbimui tinkamos medžiagos (metalai, plastikai)</li>\n        <li>Saugiai utilizuojamos pavojingos medžiagos (freonas, tepalai)</li>\n        <li>Tinkamos dalys panaudojamos pakartotinai</li>\n      </ol>\n      <p>Visi perdirbimo procesai atitinka ES aplinkosaugos reikalavimus.</p>\n    "}],t=[{question:"Kaip saugiai atsikratyti senos elektronikos?",answer:"\n      <p>Saugus elektronikos utilizavimas yra svarbus aplinkosaugai. Vilniuje, Kaune ir visoje Lietuvoje mes siūlome:</p>\n      <ul>\n        <li>Nemokamą išvežimą iš jūsų namų</li>\n        <li>Saugų duomenų sunaikinimą</li>\n        <li>Tinkamą elektronikos perdirbimą</li>\n        <li>Aplinkosaugos reikalavimų laikymąsi</li>\n      </ul>\n      <p>Užsakykite išvežimą internetu arba telefonu.</p>\n    "},{question:"Kokią elektroniką galima perdirbti?",answer:"\n      <p>Perdirbimui tinka praktiškai visa elektronika:</p>\n      <ul>\n        <li>Kompiuteriai ir jų dalys</li>\n        <li>Televizoriai ir monitoriai</li>\n        <li>Telefonai ir planšetės</li>\n        <li>Spausdintuvai</li>\n        <li>Garso aparatūra</li>\n        <li>Smulki buitinė elektronika</li>\n      </ul>\n      <p>Visa elektronika išvežama <strong>nemokamai</strong>.</p>\n    "},{question:"Ar saugiai ištrinate duomenis iš kompiuterių ir telefonų?",answer:"\n      <p>Taip, užtikriname saugų duomenų ištrynimą:</p>\n      <ul>\n        <li>Naudojame profesionalią duomenų trynimo programinę įrangą</li>\n        <li>Fiziškai sunaikiname duomenų laikmenas</li>\n        <li>Išduodame duomenų sunaikinimo sertifikatą (pagal pageidavimą)</li>\n        <li>Laikomės BDAR (GDPR) reikalavimų</li>\n      </ul>\n      <p>Jūsų duomenų saugumas yra mūsų prioritetas.</p>\n    "},{question:"Kaip vyksta elektronikos perdirbimo procesas?",answer:"\n      <p>Elektronikos perdirbimas vyksta keliais etapais:</p>\n      <ol>\n        <li>Pirminis rūšiavimas pagal elektronikos tipą</li>\n        <li>Pavojingų komponentų atskyrimas (baterijos, kondensatoriai)</li>\n        <li>Ardymas ir medžiagų atskyrimas</li>\n        <li>Vertingų metalų išgavimas</li>\n        <li>Plastiko ir stiklo perdirbimas</li>\n      </ol>\n      <p>Visas procesas atitinka ES aplinkosaugos standartus.</p>\n    "},{question:"Ar išvežate didelį kiekį elektronikos iš įmonių?",answer:"\n      <p>Taip, teikiame specializuotas paslaugas įmonėms:</p>\n      <ul>\n        <li>Išvežame bet kokį elektronikos kiekį</li>\n        <li>Pasiūlome lankstų išvežimo grafiką</li>\n        <li>Išrašome reikalingus dokumentus</li>\n        <li>Užtikriname konfidencialumą</li>\n      </ul>\n      <p>Susisiekite dėl individualaus pasiūlymo jūsų įmonei.</p>\n    "}],o=[{question:"Kiek kainuoja baldų išvežimas Kaune?",answer:"\n      <p>Baldų išvežimo kaina priklauso nuo:</p>\n      <ul>\n        <li>Baldų kiekio ir dydžio</li>\n        <li>Išnešimo sudėtingumo</li>\n        <li>Aukšto</li>\n      </ul>\n      <p>Tikslią kainą pateiksime po užklausos - susisiekite dėl nemokamos konsultacijos.</p>\n    "},{question:"Ar reikia išardyti baldus prieš išvežimą?",answer:"\n      <p>Ne, jums nereikia rūpintis baldų išardymu:</p>\n      <ul>\n        <li>Mūsų komanda profesionaliai išardys baldus</li>\n        <li>Saugiai išneš iš bet kurio aukšto</li>\n        <li>Sutvarkys išvežimo vietą</li>\n      </ul>\n      <p>Jums tereikia parodyti, kuriuos baldus reikia išvežti.</p>\n    "},{question:"Kokius baldus išvežate?",answer:"\n      <p>Išvežame visų tipų baldus:</p>\n      <ul>\n        <li>Minkštus baldus (sofos, foteliai)</li>\n        <li>Miegamojo baldus (lovos, čiužiniai)</li>\n        <li>Korpusinius baldus (spintos, komodos)</li>\n        <li>Virtuvės baldus</li>\n        <li>Biuro baldus</li>\n        <li>Lauko baldus</li>\n      </ul>\n      <p>Taip pat išvežame baldus po remonto ar kraustantis.</p>\n    "},{question:"Kaip greitai galite išvežti baldus?",answer:"\n      <p>Baldų išvežimą organizuojame operatyviai:</p>\n      <ul>\n        <li>Skubiais atvejais - tą pačią dieną</li>\n        <li>Įprastai - per 1-2 darbo dienas</li>\n        <li>Pagal išankstinį susitarimą - jums patogiu laiku</li>\n      </ul>\n      <p>Tikslų laiką suderinsime po užklausos gavimo.</p>\n    "},{question:"Ar išvežate baldus iš rūsio ar palėpės?",answer:"\n      <p>Taip, išvežame baldus iš sudėtingai pasiekiamų vietų:</p>\n      <ul>\n        <li>Rūsių ir pusrūsių</li>\n        <li>Palėpių ir mansardų</li>\n        <li>Sandėliukų</li>\n        <li>Garažų</li>\n      </ul>\n      <p>Turime specialią įrangą ir patirtį išnešti baldus iš bet kokių patalpų.</p>\n    "}],u=[{question:"Ar išvežate metalo laužą nemokamai?",answer:"\n      <p>Taip, metalo laužo išvežimas yra nemokamas, kai:</p>\n      <ul>\n  <li>Metalo kiekis viršija 100 kg</li>\n  <li>Metalas yra lengvai prieinamas</li>\n  <li>Lokacija – didesniuose miestuose (Vilnius/Kaunas) ar pagal maršrutą kitur Lietuvoje</li>\n      </ul>\n      <p>Dėl tikslių sąlygų susisiekite su mumis.</p>\n    "},{question:"Kokį metalo laužą priimate?",answer:"\n      <p>Priimame įvairų metalo laužą:</p>\n      <ul>\n        <li>Juodąjį metalą (vamzdžiai, konstrukcijos)</li>\n        <li>Spalvotąjį metalą (varis, žalvaris, aliuminis)</li>\n        <li>Nerūdijantį plieną</li>\n        <li>Automobilių dalis</li>\n        <li>Metalinius baldus ir įrangą</li>\n      </ul>\n      <p>Išvežame bet kokį metalo laužo kiekį.</p>\n    "},{question:"Ar superkate metalo laužą Kaune?",answer:"\n      <p>Taip, siūlome konkurencingas kainas už:</p>\n      <ul>\n        <li>Varį ir žalvarį</li>\n        <li>Aliuminį</li>\n        <li>Nerūdijantį plieną</li>\n        <li>Švino akumuliatorius</li>\n        <li>Elektronikos plokštes</li>\n      </ul>\n      <p>Kainos nustatomos pagal dienos metalo kursą. Susisiekite dėl tikslių kainų.</p>\n    "},{question:"Kaip paruošti metalo laužą išvežimui?",answer:"\n      <p>Optimaliam metalo laužo išvežimui rekomenduojame:</p>\n      <ul>\n        <li>Atskirti skirtingų metalų rūšis</li>\n        <li>Pašalinti nemetalines dalis</li>\n        <li>Užtikrinti priėjimą prie metalo laužo</li>\n        <li>Stambias konstrukcijas išardyti, jei įmanoma</li>\n      </ul>\n      <p>Jei negalite to padaryti, mūsų komanda padės vietoje.</p>\n    "},{question:"Ar išvežate metalo laužą iš įmonių ir gamyklų?",answer:"\n      <p>Taip, teikiame specializuotas paslaugas verslui:</p>\n      <ul>\n        <li>Reguliarus metalo laužo surinkimas</li>\n        <li>Konteinerių nuoma</li>\n        <li>Dokumentų tvarkymas</li>\n        <li>Individualūs sprendimai pagal įmonės poreikius</li>\n      </ul>\n      <p>Susisiekite dėl verslo pasiūlymo.</p>\n    "}];export{s as F,o as a,l as b,t as e,u as m};
